import { createStore } from 'vuex';

export default createStore({
  state: {
    user: null,
    token: localStorage.getItem('token') || '',
    isAuthenticated: !!localStorage.getItem('token'),
  },
  mutations: {
    setUser(state, user) {
      console.log('Setting user in Vuex:', user); 
      state.user = user;
    },
    setToken(state, token) {
      console.log('Setting token in Vuex:', token); 
      state.token = token;
      state.isAuthenticated = true;
      localStorage.setItem('token', token);
    },
    clearAuth(state) {
      console.log('Clearing auth in Vuex'); 
      state.user = null;
      state.token = '';
      state.isAuthenticated = false;
      localStorage.removeItem('token');
    },
  },
  actions: {
    login({ commit }, { token, user }) {
      console.log('Logging in via Vuex:', { token, user }); 
      commit('setToken', token);
      commit('setUser', user);
    },
    logout({ commit }) {
      commit('clearAuth');
    },
  },
});
je moj kod u store.js na frontendu, ovo je skod u profilStranica.vue na frontendu:<template>
  <div class="container user-profile" v-if="user && user.name">
    <div class="profile-info" v-if="!isEditing">
      <h1 class="profile-name">{{ user.name }}</h1>
      <h4 class="profile-email">Email: {{ user.email }}</h4>
      <button class="button1 edit-btn" @click="enableEditing">Izmijeni</button>
    </div>
    <form class="profile-form" v-else @submit.prevent="updateUserData">
      <div class="form-group">
        <label for="name">Ime:</label>
        <input
          id="name"
          v-model="editedUser.name"
          type="text"
          class="input-field"
        />
      </div>
      <div class="form-group">
        <label for="email">Email:</label>
        <input
          id="email"
          v-model="editedUser.email"
          type="text"
          class="input-field"
        />
      </div>
      <div class="form-group">
        <label for="password">Nova lozinka:</label>
        <input
          id="password"
          v-model="editedUser.password"
          type="password"
          class="input-field"
          placeholder="Unesite novu lozinku (opcionalno)"
        />
      </div>
      <div class="form-actions">
        <button class="button1 save-btn" type="submit">Spremi promjene</button>
        <button class="button1 cancel-btn" @click="disableEditing">
          Odustani
        </button>
      </div>
    </form>
  </div>
  <div class="loading" v-else>
    <p>Loading user data...</p>
  </div>
</template>

<script>
import api from "@/services/api";
import { mapState } from "vuex";

export default {
  computed: {
    ...mapState(["user", "isAuthenticated"]),
  },
  data() {
    return {
      editedUser: {},
      isEditing: false,
    };
  },
  created() {
    if (!this.user) {
      this.fetchUserData();
    } else {
      this.editedUser = { ...this.user };
    }
  },
  methods: {
    async fetchUserData() {
      try {
        const token = localStorage.getItem("token");
        const response = await api.get("/auth/korisnici", {
          headers: { Authorization: `Bearer ${token}` },
        }),
        this.$store.commit("setUser", response.data);
        this.editedUser = { ...response.data };
      } catch (error) {
        console.error("Greška pri dohvaćanju korisnika:", error);
      }
    },
    enableEditing() {
      this.isEditing = true;
    },
    disableEditing() {
      this.isEditing = false;
    },
    async updateUserData() {
  try {
    const updatedData = {
      name: this.editedUser.name,
      email: this.editedUser.email,
    };

    if (this.editedUser.password && this.editedUser.password.trim() !== "") {
      updatedData.password = this.editedUser.password;
    }

    const response = await api.put(`/auth/korisnici/${this.user.id}`, updatedData, {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    });

    // Ažuriraj Vuex stanje
    this.$store.commit("setUser", response.data);

    // Ponovno dohvaćanje korisničkih podataka
    await this.fetchUserData();

    this.isEditing = false;
    this.editedUser.password = ""; // Resetiraj polje za lozinku
  } catch (error) {
    console.error("Greška pri ažuriranju korisnika:", error.response?.data || error.message);
    alert("Došlo je do greške prilikom spremanja podataka. Provjerite unos.");
  }
}
  },
};
</script>

