.user-profile {
  margin: 20px auto;
  padding: 20px;
  border-radius: 10px;
  background-color: #fee6c1;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
}

.profile-info {
  text-align: center;
}

.profile-name {
  font-size: 2em;
  font-weight: bold;
  color: #2a231f;
}

.profile-email {
  font-size: 1.2em;
  color: #4d4d4d;
  margin-bottom: 20px;
}

.button1 {
  width: auto;
  padding: 10px 20px;
  font-size: 1em;
  font-weight: bold;
  color: #fff;
  background: #2a231f;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: 0.3s ease;
}

.button1:hover {
  background: #fbf5e5;
  color: #2a231f;
}

.input-field {
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 1em;
}

.form-group {
  margin-bottom: 15px;
}

.form-actions {
  display: flex;
  justify-content: space-between;
}

.save-btn {
  background-color: #2a231f;
}

.cancel-btn {
  background-color: #d9534f;
}

.cancel-btn:hover {
  background-color: #c9302c;
  color: #fff;
}

.loading {
  text-align: center;
  font-size: 1.5em;
  color: #2a231f;
}

.input-field {
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 1em;
}

.input-field#password {
  background-color: #fff8e1; /* Lagana žuta boja za isticanje */
}
</style>
