import { createStore } from 'vuex';

export default createStore({
  state: {
    user: null,
    token: localStorage.getItem('token') || '',
    isAuthenticated: !!localStorage.getItem('token'),
  },
  mutations: {
    setUser(state, user) {
      if (!user || !user.id) {
        console.error("Pokušaj postavljanja korisnika bez ID-a:", user);
        return;
      }
      console.log("Postavljanje korisnika u Vuex:", user);
      state.user = user;
    },
    clearUser(state) {
      console.log("Čišćenje korisničkih podataka iz Vuexa");
      state.user = null;
    },
    setToken(state, token) {
      console.log('Setting token in Vuex:', token); 
      state.token = token;
      state.isAuthenticated = true;
      localStorage.setItem('token', token);
    },
    clearAuth(state) {
      console.log('Clearing auth in Vuex'); 
      state.user = null;
      state.token = '';
      state.isAuthenticated = false;
      localStorage.removeItem('token');
    },
  },
  actions: {
    login({ commit }, { token, user }) {
      console.log('Logging in via Vuex:', { token, user }); 
      commit('setToken', token);
      commit('setUser', user);
    },
    logout({ commit }) {
      commit('clearAuth');
    },
  },
});
